                layer:"UtoyCreekWaterBasin",
                geojson:msg.payload
              };    
return msg;

var toxins = msg.payload.contaminantDetails;

// Highlight toxins that exceed EPA limits
for( var i=0; i < toxins.length; i++) {
    if( parseFloat(toxins[i].sample_amount) > parseFloat(toxins[i].toxin_limit_value) ) {
        toxins[i].toxin_type_name = "<font color='red'><b>"+toxins[i].toxin_type_name+"</b></font>";
        toxins[i].sample_amount = "<font color='red'><b>"+toxins[i].sample_amount+"</b></font>";
    }
}
msg.payload = toxins;
return msg;

